import React, { useState, useEffect } from 'react';
import './App.css';
import { Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import VotePopup from './VotePopup';

ChartJS.register(ArcElement, Tooltip, Legend);


// ‚úÖ YouTube Video ID Ï∂îÏ∂ú Ìï®Ïàò
const getYouTubeVideoId = (url) => {
  try {
    const parsedUrl = new URL(url);

    // ‚úÖ youtu.be Îã®Ï∂ï URL Ï≤òÎ¶¨
    if (parsedUrl.hostname === "youtu.be") {
      return parsedUrl.pathname.substring(1);
    }

    // ‚úÖ YouTube Shorts URL Ï≤òÎ¶¨
    if (parsedUrl.pathname.startsWith("/shorts/")) {
      return parsedUrl.pathname.replace("/shorts/", "");
    }

    // ‚úÖ ÏùºÎ∞òÏ†ÅÏù∏ YouTube URL Ï≤òÎ¶¨
    return parsedUrl.searchParams.get("v");
  } catch (error) {
    return null;
  }
};

function App() {
  const [url, setUrl] = useState('');
  const [videoId, setVideoId] = useState(null);
  const [prediction, setPrediction] = useState('');
  const [realScore, setRealScore] = useState(null);
  const [fakeScore, setFakeScore] = useState(null);
  const [resultText, setResultText] = useState(''); // ‚úÖ result_textÎ•º Ï†ÄÏû•Ìï† ÏÉÅÌÉú
  const [loading, setLoading] = useState(false);
  const [embedUrl, setEmbedUrl] = useState('');
  const [showPopup, setShowPopup] = useState(false);

  useEffect(() => {
    console.log("üìú result_text from backend:", resultText);
  }, [resultText]);
  
  const handleUrlChange = (e) => {
    const inputUrl = e.target.value;
    setUrl(inputUrl);

    const extractedVideoId = getYouTubeVideoId(inputUrl);
    console.log("Extracted Video ID:", extractedVideoId);
    setVideoId(extractedVideoId);

    const updatedUrl = inputUrl.replace('youtube.com/shorts/', 'youtube.com/watch?v=');
    setEmbedUrl(updatedUrl);
  };

  const handlePrediction = async () => {
    setLoading(true);
    try {
        console.log("üì° ÏòàÏ∏° ÏöîÏ≤≠ Ï†ÑÏÜ°:", { videoId });

        const response = await fetch(`http://localhost:8000/predict/${videoId}?video_path=${encodeURIComponent(url)}`);
        
        const data = await response.json();

        if (response.ok) {
            console.log("‚úÖ ÏòàÏ∏° ÏÑ±Í≥µ:", data);
            setPrediction(data.message);
            setRealScore(data.real_score);
            setFakeScore(data.fake_score);
            setResultText(data.result_text); // ‚úÖ Î∞±ÏóîÎìúÏóêÏÑú Î∞õÏùÄ result_text Ï†ÄÏû•
        } else {
            console.error("üö® API ÏùëÎãµ Ïò§Î•ò:", data);
            setPrediction(`Error: ${data.error || "Failed to fetch prediction"}`);
        }
    } catch (error) {
        console.error("üö® ÏÑúÎ≤Ñ Ïò§Î•ò Î∞úÏÉù:", error);
        setPrediction("Error: Failed to fetch prediction");
    } finally {
        setLoading(false);
    }
  };

  // ‚úÖ ÏòàÏ∏°Ïù¥ ÏôÑÎ£åÎêòÍ≥† videoIdÍ∞Ä Ï°¥Ïû¨ÌïòÎ©¥ ÏûêÎèôÏúºÎ°ú ÌåùÏóÖ ÎùÑÏö∞Í∏∞
  useEffect(() => {
    if (prediction && videoId) {
      console.log("‚úÖ ÏòàÏ∏° ÏôÑÎ£å! ÌåùÏóÖÏùÑ ÎùÑÏõÅÎãàÎã§.");
      setShowPopup(true);
    }
  }, [prediction, videoId]);

  const chartData = {
    labels: ['Real Video', 'Fake Video'],
    datasets: [
      {
        data: [realScore || 0, fakeScore || 0],
        backgroundColor: ['#A6DAF4', '#F4A1A7'],
        borderColor: ['#A6DAF4', '#F4A1A7'],
        borderWidth: 1,
      },
    ],
  };

  return (
    <div>
      <h1 style={{ textAlign: 'center', margin: '20px 0', color: 'rgb(194, 16, 194)' }}>IZREAL</h1>
      <input 
        type="text" 
        value={url} 
        onChange={handleUrlChange} 
        placeholder="YouTube ÏòÅÏÉÅ URLÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî." 
      />
      <button onClick={handlePrediction} disabled={loading}>
        {loading ? 'Î°úÎî© Ï§ë...' : 'Îî•ÌéòÏù¥ÌÅ¨ Ïó¨Î∂Ä ÏòàÏ∏°'}
      </button>

      {embedUrl && videoId && (
        <iframe 
          width="100%" 
          height="315" 
          src={`https://www.youtube.com/embed/${videoId}`} 
          title="YouTube video" 
          frameBorder="0" 
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" 
          allowFullScreen
        />
      )}

      {prediction && <p>{prediction}</p>}

      {realScore !== null && fakeScore !== null && (
        <div>
          <Pie data={chartData} />
          <p>üëç ÏßÑÏßú ÏòÅÏÉÅ Ï†êÏàò : {realScore.toFixed(3)}</p>
          <p>üëé Í∞ÄÏßú ÏòÅÏÉÅ Ï†êÏàò : {fakeScore.toFixed(3)}</p>
        </div>
      )}

      {/* ‚úÖ Ï§ÑÍ∏Ä ÌòïÌÉúÎ°ú result_text Ï∂úÎ†• */}
      {resultText && (
        <div className="bg-gray-100 p-4 rounded-lg mt-4">
          <h3 className="text-lg font-bold">üìã Prediction Result</h3>
          <pre className="whitespace-pre-wrap text-sm font-mono">{resultText}</pre>
        </div>
      )}

      {/* ‚úÖ Ìà¨Ìëú ÌåùÏóÖ */}
      {showPopup && videoId && (
        <VotePopup videoId={videoId} onClose={() => setShowPopup(false)} />
      )}
    </div>
  );
}

export default App;
